$(document).ready(function() {
	$('.sidebar-prosegur a').click(function(){
		$('body').find('.cx-widget').hide();
	});
    $(".menu-secundario").click(function(e) {
    	
        if (e.target == this) {
            $(this).hide();
            $('.sidebar-prosegur ul a li').removeClass('active')
            $('body').find('.cx-widget').show();
        }
    });
    $(".menu-secundario .close").click(function() {
        $(".menu-secundario").hide();
        $(".menu-secundario").css('background-color', 'rgba(0,0,0,0.0)');
        $('.sidebar-prosegur ul a li').removeClass('active');
        $('body').find('.cx-widget').show();
    });
    $(".sidebar-prosegur .close").click(function() {
        $(".sidebar-prosegur").css('left', '-100%');
        $('.sidebar-prosegur ul a li').removeClass('active');
        $('body').find('.cx-widget').show();
    });
    $(".menu-secundario .close-all").click(function() {
        $(".menu-secundario").hide();
        $(".sidebar-prosegur").css('left', '-100%');
        $('.sidebar-prosegur ul a li').removeClass('active')
    });
    $(".menu-secundario .go-back").click(function() {
        $(".sidebar-prosegur").animate({
            'left': '0%'
        }, 200);
        $(".menu-secundario").animate({
            'left': '100%'
        }, 200, function() {
            $(".navegacion-secundaria").hide()
        });
        $('.sidebar-prosegur ul a li').removeClass('active')
    });
    var menuButton = $(".sidebar-prosegur ul a");
    menuButton.click(function() {
        var menuLink = $(this).attr('href');
        $('.sidebar-prosegur ul a li').removeClass('active');
        if (menuLink.charAt(0) !== "#" || menuLink === '#' || $(menuLink).is(':visible')) {
            $(".menu-secundario").hide();
            $(".menu-secundario").css('background-color', 'rgba(0,0,0,0.0)')
        } else {
            if ($(window).width() <= 992) {
                $(".menu-secundario").css('left', '100%');
                $(".navegacion-secundaria").hide();
                $(".sidebar-prosegur").animate({
                    'left': '-100%'
                }, 200);
                $(".menu-secundario").animate({
                    'left': '0%'
                }, 200);
                $(".menu-secundario").show();
                $(".menu-secundario").animate({
                    'background-color': 'rgba(0,0,0,0.8)'
                }, 250);
                $(menuLink).show()
            } else {
                $(".navegacion-secundaria").hide();
                $(".menu-secundario").show();
                $(".menu-secundario").animate({
                    'background-color': 'rgba(0,0,0,0.8)'
                }, 250);
                $(menuLink).css('margin-left', '-415px');
                $(menuLink).show();
                $(menuLink).animate({
                    'margin-left': '0px'
                }, 200);
                $(this).children('li').addClass('active')
            }
        }
    });
    $(".header-prosegur .menu-right .ion-ios-search-strong").click(function(event) {
        if ($(window).width() <= 992) {
            event.preventDefault();
            $(".sidebar-prosegur").animate({
                'left': '0%'
            }, 200)
        } else {}
    });
    $(".header-prosegur .menu-right .input-group").mouseenter(function() {
        console.log('mouseenter');
        if ($(window).width() > 992) {
            $('.header-prosegur .menu-right .input-group input').animate({
                'width': '180px',
                'opacity': '1'
            }, 200)
        }
    });
    $(window).click(function() {
        if ($('.header-prosegur .menu-right .input-group input').is(':focus')) {
            console.log('focus')
        } else {
            setTimeout(function() {
                $('.header-prosegur .menu-right .input-group input').animate({
                    'width': '0px',
                    'opacity': '0'
                }, 200)
            }, 500)
        }
    });
    $(".header-prosegur .menu-right .input-group").mouseleave(function() {
        console.log('mouseleave');
        if ($('.header-prosegur .menu-right .input-group input').is(':focus')) {
            console.log('focus')
        } else {
            setTimeout(function() {
                $('.header-prosegur .menu-right .input-group input').animate({
                    'width': '0px',
                    'opacity': '0'
                }, 200)
            }, 500)
        }
    });
    $(".menu-left .fa-bars").click(function() {
    	$('body').find('.cx-widget').fadeOut(150);	
        $(".sidebar-prosegur").animate({
            'left': '0%'
        }, 200)
    })
})